mapper.update:
update emp_info 
	set
	ref_date = sysdate,
	status = '퇴사',
	leaving_reason = #{leaving_reason}
where empid=#{empid}

update emp_board
	set status=1
where ebnum=#{ebnum}

update emp_board 
	set subject=#{subject},
	post_content=#{post_content}
where ebnum=#{ebnum}

mapper.select:
select count(*) from post_board 
	where writer like '%${keyword}%'
	
		and board_type=#{board_type}

select * from (select rownum r, a.* from (select * from post_board a left join boardtype b on a.board_type=b.board_type where writer like '%안%'
and a.board_type = #{board_type}
order by reg desc) a) where r between #{startRow} and #{endRow}

select count(*) from post_board

	
		board_type = #{board_type}
	
	
		and status = #{status}

		select * from 
    		(select rownum r, list.* from 
		        (select * from post_board a 
		        left join boardtype b 
		        on a.board_type=b.board_type
				
					
						a.board_type = #{board_type}
					
					
						and status = #{status}
					
					
				) list)
		where r between #{startRow} and #{endRow}

select count(*) from certidate a, certischedule b 
	where a.datepk=#{datepk}
	and a.cyear=b.cyear and a.cround=b.cround and a.clevel=b.clevel

mapper.delete:
delete from certidate
where datepk in 

	#{datePK}

delete from certischedule
where 
	cnum=#{cnum}
	and cyear=#{cyear}
	and cround=#{cround}
	and clevel=#{clevel}

mapper.select:
select a.*, b.status_name from memberinfo a, member_status b 
	where a.status=b.status 
	and memid=#{memid}

select * from member_report 
	where pnum=#{pnum}
	and wr_option='C'

select count(*) from member_report a, memberinfo b
where a.memid=b.memid
 
	and b.status = #{status}

select a.*, b.email, b.mem_name, b.mobile, b.mem_point, b.regdate, c.status_name from (select memid, count(*) as reportCnt from member_report group by memid) a, memberinfo b, member_status c where a.reportCnt >= 1 and a.memid=b.memid and b.status=c.status
and b.status = #{status}
order by a.reportCnt

select * from memberinfo  where
	
		${search} like '%${keyword}%'
	
	
		and status in 
		
			#{type[index])
		
	
	
		and mem_level in 
		
			#{type[index])
		
	
	
		 and mem_point >= #{mem_point1} 
	
	
		 and mem_point <= #{mem_point2} 
	
	
		and regdate between #{regDate1} and #{regDate2}
	
	order by

select * from (select rownum r, list.* from (select a.*, b.status_name from memberinfo a, member_status b where a.status=b.status
and a.status=#{status}
order by regdate desc ) list) where r between #{startRow} and #{endRow}

mapper.update:
update certiinfo
cname=#{cname}, category=#{category}, clevel=#{clevel}, company = NVL(#{company}, company), website = NVL(#{website}, website), price = NVL(#{price}, price), cmethod = NVL(#{cmethod}, cmethod), subject = NVL(#{subject}, subject), cutline = NVL(#{cutline}, cutline), cinfo = NVL(#{cinfo}, cinfo), cjob = NVL(#{cjob}, cjob), ncs_cat = NVL(#{ncs_cat}, ncs_cat), notice = NVL(#{notice}, notice)
where cnum=#{cnum}

update certi_requirement
req_degree = NVL(#{req_degree}, req_degree), req_age = NVL(#{req_age}, req_age), req_exp = NVL(#{req_exp}, req_exp), pre_requisite = NVL(#{pre_requisite}, pre_requisite), ref = NVL(#{ref}, ref)
where cnum=#{cnum}

mapper.select:
select * from certidate 
where clevel=#{clevel}
and cyear in

	#{cyear}

and cround in 

	#{cround}

select * from certidate 
	where cyear=#{cyear}
	and cround=#{cround}
	and clevel=#{clevel}

		select * from (select rownum r, list.* from (select *
		from certiinfo 
			
        		where category = #{category}
        	
			
				order by ${sort} ${order}
			
			
				order by cnum asc
			
			nulls last) list)
        where r between #{startRow} and #{endRow}

select cnum,cname,category,clevel,cround, to_char(regStart,'YYYY-MM-DD') as regStart, to_char(regEnd,'YYYY-MM-DD') as regEnd, to_char(reg_addStart,'YYYY-MM-DD') as reg_addStart, to_char(reg_addEnd,'YYYY-MM-DD') as reg_addEnd, to_char(regStartTime,'HH24:MI') as regStartTime, to_char(regEndTime,'HH24:MI') as regEndTime, to_char(testDate,'YYYY-MM-DD') as testDate, to_char(resDate,'YYYY-MM-DD') as resDate from certiinfo where cnum in
#{cnum}
order by cnum
