mapper.select:
		select * from 
    		(select rownum r, list.* from 
		        (select * from certiinfo a 
		        left join certi_requirement b 
		        on a.cnum=b.cnum


						category=#{category} 


			         or req_degree in

			            #{degree}



			         or req_age in

			            #{age}



			         or req_exp in

			            #{exp}



						clevel=#{clevel} 


				) list)
		where r between #{startRow} and #{endRow}

