mapper.update:
update MemberInfo
passwd = #{passwd}, mem_name = #{mem_name}, email=#{email}, BIRTHDAY=TO_String(#{birthday},'YYYY-MM-DD'), gender=#{gender}, postalCode=#{postalCode}, address=#{address}, addr_detail=#{addr_detail}, mobile=#{mobile}, mem_degree=#{mem_degree}, major=#{major}, mem_job=#{mem_job}, passwdQ1=#{passwdQ1}, passwdQ2=#{passwdQ2}
where memid=#{memid} and status in (0,1)
