select a.cname, b.cnum, b.cyear, b.cround, b.clevel, TO_CHAR(b.docRegStart1,'""YYYY-MM-DD"T"HH24:MI') as docRegStart1, TO_CHAR(b.docRegEnd1,'""YYYY-MM-DD"T"HH24:MI') as docRegEnd1, TO_CHAR(b.docRegStart2,'""YYYY-MM-DD"T"HH24:MI') as docRegStart2, TO_CHAR(b.docRegEnd2,'""YYYY-MM-DD"T"HH24:MI') as docRegEnd2, TO_CHAR(b.docTestStart,'""YYYY-MM-DD"T"HH24:MI') as docTestStart, TO_CHAR(b.docTestEnd,'""YYYY-MM-DD"T"HH24:MI') as docTestEnd, TO_CHAR(b.docResultStart,'""YYYY-MM-DD"T"HH24:MI') as docResultStart, TO_CHAR(b.docResultEnd,'""YYYY-MM-DD"T"HH24:MI') as docResultEnd, TO_CHAR(b.docSubmitStart,'""YYYY-MM-DD"T"HH24:MI') as docSubmitStart, TO_CHAR(b.docSubmitEnd,'""YYYY-MM-DD"T"HH24:MI') as docSubmitEnd, TO_CHAR(b.pracRegStart1,'""YYYY-MM-DD"T"HH24:MI') as pracRegStart1, TO_CHAR(b.pracRegEnd1,'""YYYY-MM-DD"T"HH24:MI') as pracRegEnd1, TO_CHAR(b.pracRegStart2,'""YYYY-MM-DD"T"HH24:MI') as pracRegStart2, TO_CHAR(b.pracRegEnd2,'""YYYY-MM-DD"T"HH24:MI') as pracRegEnd2, TO_CHAR(b.pracTestStart,'""YYYY-MM-DD"T"HH24:MI') as pracTestStart, TO_CHAR(b.pracTestEnd,'""YYYY-MM-DD"T"HH24:MI') as pracTestEnd, TO_CHAR(b.pracResStart,'""YYYY-MM-DD"T"HH24:MI') as pracResStart, TO_CHAR(b.pracResEnd,'""YYYY-MM-DD"T"HH24:MI') as pracResEnd from certiinfo a, certidate b where a.cnum=b.cnum
and b.cnum in
#{cnum}

select d.cname, c.* from (select a.cnum, b.cyear, b.cround, b.clevel, TO_CHAR(b.docRegStart1,'""YYYY-MM-DD"T"HH24:MI') as docRegStart1, TO_CHAR(b.docRegEnd1,'""YYYY-MM-DD"T"HH24:MI') as docRegEnd1, TO_CHAR(b.docRegStart2,'""YYYY-MM-DD"T"HH24:MI') as docRegStart2, TO_CHAR(b.docRegEnd2,'""YYYY-MM-DD"T"HH24:MI') as docRegEnd2, TO_CHAR(b.docTestStart,'""YYYY-MM-DD"T"HH24:MI') as docTestStart, TO_CHAR(b.docTestEnd,'""YYYY-MM-DD"T"HH24:MI') as docTestEnd, TO_CHAR(b.docResultStart,'""YYYY-MM-DD"T"HH24:MI') as docResultStart, TO_CHAR(b.docResultEnd,'""YYYY-MM-DD"T"HH24:MI') as docResultEnd, TO_CHAR(b.docSubmitStart,'""YYYY-MM-DD"T"HH24:MI') as docSubmitStart, TO_CHAR(b.docSubmitEnd,'""YYYY-MM-DD"T"HH24:MI') as docSubmitEnd, TO_CHAR(b.pracRegStart1,'""YYYY-MM-DD"T"HH24:MI') as pracRegStart1, TO_CHAR(b.pracRegEnd1,'""YYYY-MM-DD"T"HH24:MI') as pracRegEnd1, TO_CHAR(b.pracRegStart2,'""YYYY-MM-DD"T"HH24:MI') as pracRegStart2, TO_CHAR(b.pracRegEnd2,'""YYYY-MM-DD"T"HH24:MI') as pracRegEnd2, TO_CHAR(b.pracTestStart,'""YYYY-MM-DD"T"HH24:MI') as pracTestStart, TO_CHAR(b.pracTestEnd,'""YYYY-MM-DD"T"HH24:MI') as pracTestEnd, TO_CHAR(b.pracResStart,'""YYYY-MM-DD"T"HH24:MI') as pracResStart, TO_CHAR(b.pracResEnd,'""YYYY-MM-DD"T"HH24:MI') as pracResEnd from certischedule a, certidate b where a.cnum in
#{cnum}
and a.cyear=b.cyear and a.cround=b.cround and a.clevel=b.clevel) c left join certiinfo d on c.cnum=d.cnum
