mapper.insert:
insert into certiDate values( #{cnum, jdbcType=VARCHAR}, #{cyear}, #{cround}, #{clevel, jdbcType=VARCHAR},
TO_DATE(#{docRegStart1, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{docRegEnd1, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{docRegStart2, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{docRegEnd2, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{docTestStart, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{docTestEnd, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{docResultStart, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{docResultEnd, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{docSubmitStart, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{docSubmitEnd, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{pracRegStart1, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{pracRegEnd1, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{pracRegStart2, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{pracRegEnd2, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{pracTestStart, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{pracTestEnd, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{pracResStart, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI'), TO_DATE(#{pracResEnd, jdbcType=VARCHAR}, 'YYYY-MM-DD HH24:MI')
)

mapper.update:
update certiinfo
clevel=#{dto.clevel}, company = #{dto.company}, website=#{dto.website}, requirement=#{dto.requirement}, cmethod=#{dto.cmethod}, subject=#{dto.subject}, cutline=#{dto.cutline}, cinfo=#{dto.cinfo}, cjob=#{dto.cjob}, expiry=to_date(#{dto.expiry},'YYYY-MM-DD'), status=#{dto.status}, price=#{dto.price}, ncs_cat=#{dto.ncs_cat}, notice=#{dto.notice}
where cname=#{cname}
